mapper.select:
select dept.id, dept.name, dept.description from abw_admin_dept dept
where dept.name LIKE '%'||#{dept.name}||'%'
ORDER BY dept.id desc limit #{page.iDisplayStart},#{page.iDisplayLength}

mapper.update:
UPDATE abw_admin_dept

      name=#{name},
      description=#{description}

WHERE id=#{id}
